mapper.update:
UPDATE QA 
SET q_status=#{qStatus}, q_answer=#{qAnswer} 
WHERE q_id=#{qId}

update service 
set s_kind=#{sKind}, s_cate=#{sCate}, s_title=#{sTitle}, s_detail=#{sDetail} 
where s_id=#{sId}

update deliver 
set d_status=#{dStatus} 
where d_id=#{dId}

update deliver 
set d_status=#{dStatus}, d_cancel='T' 
where d_id=#{dId}
